import express, { type Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { ObjectStorageService, ObjectNotFoundError } from "./objectStorage";
import { analyzeDocument, extractTextFromImage } from "./openai";
import { z } from "zod";
import { insertDocumentSchema } from "@shared/schema";
import multer from "multer";
import { setupAuth, isAuthenticated } from "./replitAuth";
import Stripe from "stripe";
import { createHmac } from "crypto";

// Initialize Stripe (we'll ask for secrets later)
let stripe: Stripe | null = null;
if (process.env.STRIPE_SECRET_KEY) {
  stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
    apiVersion: "2025-08-27.basil",
  });
}

// Webhook signature verification
function verifyWebhookSignature(rawBody: string, signature: string, secret: string): boolean {
  const expectedSignature = createHmac('sha256', secret)
    .update(rawBody)
    .digest('hex');
  return `sha256=${expectedSignature}` === signature;
}

// Price validation schema
const priceConfig = {
  weekly: { amount: 299, label: "Weekly" },
  monthly: { amount: 998, label: "Monthly" },
  yearly: { amount: 9997, label: "Yearly" },
} as const;

// Input validation schemas
const createSubscriptionSchema = z.object({
  priceType: z.enum(["weekly", "monthly", "yearly"]),
});

const confirmSubscriptionSchema = z.object({
  paymentIntentId: z.string().min(1),
  subscriptionType: z.enum(["weekly", "monthly", "yearly"]),
});

const upload = multer({ storage: multer.memoryStorage() });

// Webhook helper functions
async function handlePaymentSuccess(paymentIntent: any) {
  try {
    const { userId, subscriptionType } = paymentIntent.metadata;

    if (!userId || !subscriptionType) {
      console.error('Missing metadata in payment intent:', paymentIntent.id);
      return;
    }

    const user = await storage.getUser(userId);
    if (!user) {
      console.error('User not found for payment:', userId);
      return;
    }

    // Calculate subscription period
    const now = new Date();
    let periodEnd = new Date();

    switch (subscriptionType) {
      case 'weekly':
        periodEnd.setDate(now.getDate() + 7);
        break;
      case 'monthly':
        periodEnd.setMonth(now.getMonth() + 1);
        break;
      case 'yearly':
        periodEnd.setFullYear(now.getFullYear() + 1);
        break;
    }

    // Create subscription record
    const priceInfo = priceConfig[subscriptionType as keyof typeof priceConfig];
    await storage.createSubscription({
      userId,
      stripeSubscriptionId: paymentIntent.id,
      stripeCustomerId: paymentIntent.customer,
      status: "active",
      type: subscriptionType,
      amount: priceInfo.amount,
      currency: "eur",
      currentPeriodStart: now,
      currentPeriodEnd: periodEnd,
      cancelAtPeriodEnd: 0,
    });

    // Update user status
    await storage.upsertUser({
      ...user,
      subscriptionStatus: "active",
      subscriptionType,
    });

    console.log(`Subscription activated for user ${userId}`);
  } catch (error) {
    console.error('Error handling payment success:', error);
  }
}

async function handlePaymentFailure(paymentIntent: any) {
  try {
    const { userId } = paymentIntent.metadata;

    if (userId) {
      console.log(`Payment failed for user ${userId}:`, paymentIntent.last_payment_error?.message);
      // Could send notification email here
    }
  } catch (error) {
    console.error('Error handling payment failure:', error);
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  const objectStorageService = new ObjectStorageService();

  // Setup authentication
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Stripe webhook endpoint (before authentication middleware)
  if (stripe && process.env.STRIPE_WEBHOOK_SECRET) {
    app.post('/api/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
      try {
        const signature = req.headers['stripe-signature'] as string;
        const rawBody = req.body.toString();

        if (!verifyWebhookSignature(rawBody, signature, process.env.STRIPE_WEBHOOK_SECRET!)) {
          console.error('Invalid webhook signature');
          return res.status(400).send('Invalid signature');
        }

        const event = JSON.parse(rawBody);
        console.log('Stripe webhook received:', event.type);

        switch (event.type) {
          case 'payment_intent.succeeded':
            await handlePaymentSuccess(event.data.object);
            break;
          case 'payment_intent.payment_failed':
            await handlePaymentFailure(event.data.object);
            break;
          default:
            console.log(`Unhandled webhook event type: ${event.type}`);
        }

        res.json({ received: true });
      } catch (error: any) {
        console.error('Webhook error:', error);
        res.status(400).send(`Webhook Error: ${error.message}`);
      }
    });
  }

  // Stripe subscription routes
  if (stripe) {
    // Get or create subscription for the user
    app.post('/api/subscription/create', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;

        // Validate input
        const parseResult = createSubscriptionSchema.safeParse(req.body);
        if (!parseResult.success) {
          return res.status(400).json({ 
            message: "Invalid input",
            errors: parseResult.error.errors
          });
        }

        const { priceType } = parseResult.data;

        const user = await storage.getUser(userId);
        if (!user) {
          return res.status(404).json({ message: "User not found" });
        }

        const priceInfo = priceConfig[priceType];
        if (!priceInfo) {
          return res.status(400).json({ message: "Invalid price type" });
        }

        let customerId = user.stripeCustomerId;

        // Create Stripe customer if doesn't exist
        if (!customerId) {
          const customer = await stripe!.customers.create({
            email: user.email || undefined,
            name: user.firstName && user.lastName ? `${user.firstName} ${user.lastName}` : undefined,
          });
          customerId = customer.id;
          await storage.updateUserStripeInfo(userId, customerId);
        }

        // Create payment intent with enhanced security
        const paymentIntent = await stripe!.paymentIntents.create({
          amount: priceInfo.amount,
          currency: "eur",
          customer: customerId,
          automatic_payment_methods: {
            enabled: true,
          },
          metadata: {
            userId,
            subscriptionType: priceType,
            createdAt: new Date().toISOString(),
          },
          description: `AdminIA ${priceInfo.label} Subscription`,
        });

        res.json({ clientSecret: paymentIntent.client_secret });
      } catch (error: any) {
        console.error("Error creating subscription:", error);
        res.status(500).json({ message: "Error creating subscription: " + error.message });
      }
    });

    // Confirm subscription payment
    app.post('/api/subscription/confirm', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;

        // Validate input
        const parseResult = confirmSubscriptionSchema.safeParse(req.body);
        if (!parseResult.success) {
          return res.status(400).json({ 
            message: "Invalid input",
            errors: parseResult.error.errors
          });
        }

        const { paymentIntentId, subscriptionType } = parseResult.data;

        const user = await storage.getUser(userId);
        if (!user) {
          return res.status(404).json({ message: "User not found" });
        }

        // Update user subscription status
        const now = new Date();
        let periodEnd = new Date();

        switch (subscriptionType) {
          case 'weekly':
            periodEnd.setDate(now.getDate() + 7);
            break;
          case 'monthly':
            periodEnd.setMonth(now.getMonth() + 1);
            break;
          case 'yearly':
            periodEnd.setFullYear(now.getFullYear() + 1);
            break;
        }

        // Verify payment intent exists and is successful
        const paymentIntent = await stripe!.paymentIntents.retrieve(paymentIntentId);
        if (paymentIntent.status !== 'succeeded') {
          return res.status(400).json({ message: "Payment not completed" });
        }

        // Create subscription record
        const priceInfo = priceConfig[subscriptionType];
        const subscription = await storage.createSubscription({
          userId,
          stripeSubscriptionId: paymentIntentId,
          stripeCustomerId: user.stripeCustomerId!,
          status: "active",
          type: subscriptionType,
          amount: priceInfo.amount,
          currency: "eur",
          currentPeriodStart: now,
          currentPeriodEnd: periodEnd,
          cancelAtPeriodEnd: 0,
        });

        // Update user
        await storage.upsertUser({
          ...user,
          subscriptionStatus: "active",
          subscriptionType,
        });

        res.json({ subscription });
      } catch (error: any) {
        console.error("Error confirming subscription:", error);
        res.status(500).json({ message: "Error confirming subscription: " + error.message });
      }
    });

    // Get user subscription status
    app.get('/api/subscription/status', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;
        const user = await storage.getUser(userId);
        const subscription = await storage.getUserActiveSubscription(userId);

        res.json({
          user: {
            subscriptionStatus: user?.subscriptionStatus || "trial",
            subscriptionType: user?.subscriptionType,
            trialEndsAt: user?.trialEndsAt,
          },
          subscription,
        });
      } catch (error: any) {
        console.error("Error getting subscription status:", error);
        res.status(500).json({ message: "Error getting subscription status: " + error.message });
      }
    });

    // Cancel subscription
    app.post('/api/subscription/cancel', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;
        const subscription = await storage.getUserActiveSubscription(userId);

        if (!subscription) {
          return res.status(404).json({ message: "No active subscription found" });
        }

        await storage.cancelSubscription(subscription.id);

        // Update user status
        const user = await storage.getUser(userId);
        if (user) {
          await storage.upsertUser({
            ...user,
            subscriptionStatus: "canceled",
          });
        }

        res.json({ message: "Subscription canceled successfully" });
      } catch (error: any) {
        console.error("Error canceling subscription:", error);
        res.status(500).json({ message: "Error canceling subscription: " + error.message });
      }
    });
  }

  // Serve private objects
  app.get("/objects/:objectPath(*)", async (req, res) => {
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(req.path);
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error serving object:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  // Get upload URL for document (protected)
  app.post("/api/documents/upload-url", isAuthenticated, async (req, res) => {
    try {
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error getting upload URL:", error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });

  // Create document after upload (protected)
  app.post("/api/documents", isAuthenticated, async (req, res) => {
    try {
      const validatedData = insertDocumentSchema.parse(req.body);

      // Normalize object path
      const objectPath = objectStorageService.normalizeObjectEntityPath(validatedData.objectPath);

      const document = await storage.createDocument({
        ...validatedData,
        objectPath,
      });

      // Queue for AI processing
      await storage.createAiProcessingQueue({
        documentId: document.id,
        status: "pending"
      });

      res.json(document);
    } catch (error) {
      console.error("Error creating document:", error);
      res.status(400).json({ error: "Invalid document data" });
    }
  });

  // Get all documents (protected)
  app.get("/api/documents", isAuthenticated, async (req, res) => {
    try {
      const documents = await storage.getDocuments();
      res.json(documents);
    } catch (error) {
      console.error("Error getting documents:", error);
      res.status(500).json({ error: "Failed to get documents" });
    }
  });

  // Get document by ID (protected)
  app.get("/api/documents/:id", isAuthenticated, async (req, res) => {
    try {
      const document = await storage.getDocument(req.params.id);
      if (!document) {
        return res.status(404).json({ error: "Document not found" });
      }
      res.json(document);
    } catch (error) {
      console.error("Error getting document:", error);
      res.status(500).json({ error: "Failed to get document" });
    }
  });

  // Delete document (protected)
  app.delete("/api/documents/:id", isAuthenticated, async (req, res) => {
    try {
      await storage.deleteDocument(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting document:", error);
      res.status(500).json({ error: "Failed to delete document" });
    }
  });

  // Process document with AI (protected)
  app.post("/api/documents/:id/analyze", isAuthenticated, async (req, res) => {
    try {
      const document = await storage.getDocument(req.params.id);
      if (!document) {
        return res.status(404).json({ error: "Document not found" });
      }

      // Update status to processing
      await storage.updateDocument(document.id, { status: "processing" });

      let analysis;
      if (document.mimeType.startsWith('image/')) {
        // For images, extract text first then analyze
        const objectFile = await objectStorageService.getObjectEntityFile(document.objectPath);

        // Get image data (simplified - in production you'd stream this properly)
        const [metadata] = await objectFile.getMetadata();
        const imageStream = objectFile.createReadStream();
        const chunks: Buffer[] = [];

        for await (const chunk of imageStream) {
          chunks.push(chunk);
        }

        const imageBuffer = Buffer.concat(chunks);
        const base64Image = imageBuffer.toString('base64');

        const extractedText = await extractTextFromImage(base64Image);
        analysis = await analyzeDocument(extractedText, base64Image);
      } else {
        // For text documents, analyze directly (you'd need to implement text extraction for PDFs, etc.)
        analysis = await analyzeDocument("Document content would be extracted here");
      }

      // Update document with analysis results
      await storage.updateDocument(document.id, {
        status: "completed",
        category: analysis.category,
        aiAnalysis: analysis,
        extractedData: analysis.extractedData,
      });

      res.json(analysis);
    } catch (error) {
      console.error("Error analyzing document:", error);

      // Update status to error
      await storage.updateDocument(req.params.id, { status: "error" });

      res.status(500).json({ error: "Failed to analyze document" });
    }
  });

  // Get categories (public)
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error getting categories:", error);
      res.status(500).json({ error: "Failed to get categories" });
    }
  });

  // Get document statistics (protected)
  app.get("/api/statistics", isAuthenticated, async (req, res) => {
    try {
      const stats = await storage.getDocumentStatistics();
      res.json(stats);
    } catch (error) {
      console.error("Error getting statistics:", error);
      res.status(500).json({ error: "Failed to get statistics" });
    }
  });

  // Get AI processing queue status (protected)
  app.get("/api/ai-queue", isAuthenticated, async (req, res) => {
    try {
      const queue = await storage.getAiProcessingQueue();
      res.json(queue);
    } catch (error) {
      console.error("Error getting AI queue:", error);
      res.status(500).json({ error: "Failed to get AI queue" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type Document, type InsertDocument, type Category, type InsertCategory, type AiProcessingQueue, type InsertAiProcessingQueue, type User, type UpsertUser, type Subscription, type InsertSubscription } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Users - (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserStripeInfo(id: string, stripeCustomerId: string, stripeSubscriptionId?: string): Promise<User | undefined>;

  // Subscriptions
  getSubscription(id: string): Promise<Subscription | undefined>;
  createSubscription(subscription: InsertSubscription): Promise<Subscription>;
  updateSubscription(id: string, updates: Partial<Subscription>): Promise<Subscription | undefined>;
  getUserActiveSubscription(userId: string): Promise<Subscription | undefined>;
  cancelSubscription(subscriptionId: string): Promise<void>;

  // Documents
  getDocument(id: string): Promise<Document | undefined>;
  getDocuments(): Promise<Document[]>;
  createDocument(document: InsertDocument): Promise<Document>;
  updateDocument(id: string, updates: Partial<Document>): Promise<Document | undefined>;
  deleteDocument(id: string): Promise<void>;
  getDocumentsByCategory(category: string): Promise<Document[]>;
  getRecentDocuments(limit?: number): Promise<Document[]>;

  // Categories
  getCategories(): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // AI Processing Queue
  getAiProcessingQueue(): Promise<AiProcessingQueue[]>;
  createAiProcessingQueue(item: InsertAiProcessingQueue): Promise<AiProcessingQueue>;
  updateAiProcessingQueue(id: string, updates: Partial<AiProcessingQueue>): Promise<AiProcessingQueue | undefined>;

  // Statistics
  getDocumentStatistics(): Promise<{
    totalDocuments: number;
    processedToday: number;
    pendingProcessing: number;
    categoryCounts: Record<string, number>;
  }>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private subscriptions: Map<string, Subscription>;
  private documents: Map<string, Document>;
  private categories: Map<string, Category>;
  private aiQueue: Map<string, AiProcessingQueue>;

  constructor() {
    this.users = new Map();
    this.subscriptions = new Map();
    this.documents = new Map();
    this.categories = new Map();
    this.aiQueue = new Map();

    // Initialize default categories
    this.initializeDefaultCategories();
  }

  // User operations - (IMPORTANT) these user operations are mandatory for Replit Auth.
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const existingUser = this.users.get(userData.id!);
    const now = new Date();

    const user: User = {
      ...existingUser,
      ...userData,
      id: userData.id!,
      email: userData.email ?? null,
      firstName: userData.firstName ?? null,
      lastName: userData.lastName ?? null,
      profileImageUrl: userData.profileImageUrl ?? null,
      stripeCustomerId: userData.stripeCustomerId ?? null,
      stripeSubscriptionId: userData.stripeSubscriptionId ?? null,
      subscriptionStatus: userData.subscriptionStatus ?? existingUser?.subscriptionStatus ?? "trial",
      subscriptionType: userData.subscriptionType ?? null,
      trialEndsAt: userData.trialEndsAt ?? null,
      createdAt: existingUser?.createdAt ?? now,
      updatedAt: now,
    };

    this.users.set(user.id, user);
    return user;
  }

  async updateUserStripeInfo(id: string, stripeCustomerId: string, stripeSubscriptionId?: string): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser: User = {
      ...user,
      stripeCustomerId,
      stripeSubscriptionId: stripeSubscriptionId ?? null,
      updatedAt: new Date(),
    };

    this.users.set(id, updatedUser);
    return updatedUser;
  }

  // Subscription operations
  async getSubscription(id: string): Promise<Subscription | undefined> {
    return this.subscriptions.get(id);
  }

  async createSubscription(subscriptionData: InsertSubscription): Promise<Subscription> {
    const id = randomUUID();
    const now = new Date();
    const subscription: Subscription = {
      ...subscriptionData,
      id,
      currency: subscriptionData.currency || "eur",
      currentPeriodStart: subscriptionData.currentPeriodStart ?? null,
      currentPeriodEnd: subscriptionData.currentPeriodEnd ?? null,
      cancelAtPeriodEnd: subscriptionData.cancelAtPeriodEnd ?? 0,
      createdAt: now,
      updatedAt: now,
    };
    this.subscriptions.set(id, subscription);
    return subscription;
  }

  async updateSubscription(id: string, updates: Partial<Subscription>): Promise<Subscription | undefined> {
    const subscription = this.subscriptions.get(id);
    if (!subscription) return undefined;

    const updatedSubscription: Subscription = {
      ...subscription,
      ...updates,
      updatedAt: new Date(),
    };
    this.subscriptions.set(id, updatedSubscription);
    return updatedSubscription;
  }

  async getUserActiveSubscription(userId: string): Promise<Subscription | undefined> {
    return Array.from(this.subscriptions.values())
      .find(sub => sub.userId === userId && sub.status === "active");
  }

  async cancelSubscription(subscriptionId: string): Promise<void> {
    const subscription = this.subscriptions.get(subscriptionId);
    if (subscription) {
      const updatedSubscription: Subscription = {
        ...subscription,
        status: "canceled",
        cancelAtPeriodEnd: 1,
        updatedAt: new Date(),
      };
      this.subscriptions.set(subscriptionId, updatedSubscription);
    }
  }

  private initializeDefaultCategories() {
    const defaultCategories: InsertCategory[] = [
      { name: "factures", icon: "fas fa-file-invoice", color: "blue" },
      { name: "contrats", icon: "fas fa-file-contract", color: "green" },
      { name: "medical", icon: "fas fa-file-medical", color: "purple" },
      { name: "legal", icon: "fas fa-gavel", color: "red" },
      { name: "correspondence", icon: "fas fa-envelope", color: "yellow" },
      { name: "financial", icon: "fas fa-chart-line", color: "indigo" },
      { name: "administrative", icon: "fas fa-building", color: "gray" },
      { name: "other", icon: "fas fa-file-alt", color: "orange" },
    ];

    defaultCategories.forEach(category => {
      const id = randomUUID();
      const categoryWithId: Category = {
        ...category,
        id,
        createdAt: new Date(),
      };
      this.categories.set(id, categoryWithId);
    });
  }

  async getDocument(id: string): Promise<Document | undefined> {
    return this.documents.get(id);
  }

  async getDocuments(): Promise<Document[]> {
    return Array.from(this.documents.values()).sort(
      (a, b) => b.createdAt.getTime() - a.createdAt.getTime()
    );
  }

  async createDocument(insertDocument: InsertDocument): Promise<Document> {
    const id = randomUUID();
    const now = new Date();
    const document: Document = {
      ...insertDocument,
      id,
      status: insertDocument.status || "pending",
      category: insertDocument.category ?? null,
      aiAnalysis: insertDocument.aiAnalysis ?? null,
      extractedData: insertDocument.extractedData ?? null,
      createdAt: now,
      updatedAt: now,
    };
    this.documents.set(id, document);
    return document;
  }

  async updateDocument(id: string, updates: Partial<Document>): Promise<Document | undefined> {
    const document = this.documents.get(id);
    if (!document) return undefined;

    const updatedDocument: Document = {
      ...document,
      ...updates,
      updatedAt: new Date(),
    };
    this.documents.set(id, updatedDocument);
    return updatedDocument;
  }

  async deleteDocument(id: string): Promise<void> {
    this.documents.delete(id);
    // Also remove from AI queue
    const queueEntries = Array.from(this.aiQueue.entries());
    for (const [queueId, item] of queueEntries) {
      if (item.documentId === id) {
        this.aiQueue.delete(queueId);
      }
    }
  }

  async getDocumentsByCategory(category: string): Promise<Document[]> {
    return Array.from(this.documents.values())
      .filter(doc => doc.category === category)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getRecentDocuments(limit: number = 10): Promise<Document[]> {
    return Array.from(this.documents.values())
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(0, limit);
  }

  async getCategories(): Promise<Category[]> {
    return Array.from(this.categories.values());
  }

  async createCategory(insertCategory: InsertCategory): Promise<Category> {
    const id = randomUUID();
    const category: Category = {
      ...insertCategory,
      id,
      createdAt: new Date(),
    };
    this.categories.set(id, category);
    return category;
  }

  async getAiProcessingQueue(): Promise<AiProcessingQueue[]> {
    return Array.from(this.aiQueue.values()).sort(
      (a, b) => a.createdAt.getTime() - b.createdAt.getTime()
    );
  }

  async createAiProcessingQueue(insertItem: InsertAiProcessingQueue): Promise<AiProcessingQueue> {
    const id = randomUUID();
    const now = new Date();
    const item: AiProcessingQueue = {
      ...insertItem,
      id,
      status: insertItem.status || "pending",
      result: insertItem.result ?? null,
      error: insertItem.error ?? null,
      createdAt: now,
      updatedAt: now,
    };
    this.aiQueue.set(id, item);
    return item;
  }

  async updateAiProcessingQueue(id: string, updates: Partial<AiProcessingQueue>): Promise<AiProcessingQueue | undefined> {
    const item = this.aiQueue.get(id);
    if (!item) return undefined;

    const updatedItem: AiProcessingQueue = {
      ...item,
      ...updates,
      updatedAt: new Date(),
    };
    this.aiQueue.set(id, updatedItem);
    return updatedItem;
  }

  async getDocumentStatistics(): Promise<{
    totalDocuments: number;
    processedToday: number;
    pendingProcessing: number;
    categoryCounts: Record<string, number>;
  }> {
    const documents = Array.from(this.documents.values());
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const processedToday = documents.filter(
      doc => doc.updatedAt >= today && doc.status === "completed"
    ).length;

    const categoryCounts: Record<string, number> = {};
    documents.forEach(doc => {
      if (doc.category) {
        categoryCounts[doc.category] = (categoryCounts[doc.category] || 0) + 1;
      }
    });

    const pendingProcessing = Array.from(this.aiQueue.values()).filter(
      item => item.status === "pending" || item.status === "processing"
    ).length;

    return {
      totalDocuments: documents.length,
      processedToday,
      pendingProcessing,
      categoryCounts,
    };
  }
}

export const storage = new MemStorage();

