import { useQuery, useMutation, useQueryClient, UseQueryOptions, UseMutationOptions } from '@tanstack/react-query';
import { apiClient, ApiError } from '@/services/apiClient';

// Custom hook for GET requests
export function useApiQuery<TData = unknown, TError = ApiError>(
  queryKey: (string | number)[],
  endpoint: string,
  options?: Omit<UseQueryOptions<TData, TError>, 'queryKey' | 'queryFn'>
) {
  return useQuery<TData, TError>({
    queryKey,
    queryFn: () => apiClient.get<TData>(endpoint),
    ...options,
  });
}

// Custom hook for POST/PUT/DELETE requests
export function useApiMutation<TData = unknown, TVariables = unknown, TError = ApiError>(
  mutationFn: (variables: TVariables) => Promise<TData>,
  options?: UseMutationOptions<TData, TError, TVariables>
) {
  const queryClient = useQueryClient();

  return useMutation<TData, TError, TVariables>({
    mutationFn,
    ...options,
    onSuccess: (data, variables, context) => {
      // Call the original onSuccess if provided
      options?.onSuccess?.(data, variables, context);

      // You can add global success handling here
    },
    onError: (error, variables, context) => {
      // Call the original onError if provided
      options?.onError?.(error, variables, context);

      // You can add global error handling here
      console.error('API Mutation Error:', error);
    },
  });
}

// Convenience hooks for common operations
export function useCreateMutation<TData = unknown, TVariables = unknown>(
  endpoint: string,
  options?: UseMutationOptions<TData, ApiError, TVariables>
) {
  return useApiMutation(
    (data: TVariables) => apiClient.post<TData>(endpoint, data),
    options
  );
}

export function useUpdateMutation<TData = unknown, TVariables = unknown>(
  endpoint: string,
  options?: UseMutationOptions<TData, ApiError, TVariables>
) {
  return useApiMutation(
    (data: TVariables) => apiClient.put<TData>(endpoint, data),
    options
  );
}

export function useDeleteMutation<TData = unknown>(
  endpoint: string,
  options?: UseMutationOptions<TData, ApiError, void>
) {
  return useApiMutation(
    () => apiClient.delete<TData>(endpoint),
    options
  );
}

mobile/src/hooks/useAuth.ts.content:
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useApiQuery, useApiMutation } from '@/hooks/useApi';
import { API_ENDPOINTS } from '@/constants/api';
import { authService } from '@/services/authService';
import type { User } from '@shared/schema';

interface AuthUser extends User {
  isAuthenticated: boolean;
}

export function useAuth() {
  const queryClient = useQueryClient();

  // Get current user
  const {
    data: user,
    isLoading,
    error,
    refetch,
  } = useApiQuery<User>(['auth', 'user'], API_ENDPOINTS.AUTH, {
    retry: false,
    staleTime: 5 * 60 * 1000, // 5 minutes
    refetchOnMount: true,
    refetchOnReconnect: true,
  });

  // Login mutation using the auth service
  const loginMutation = useApiMutation<boolean, void>(
    async () => {
      const success = await authService.login();
      if (success) {
        // Refresh user data after successful login
        await refetch();
      }
      return success;
    },
    {
      onSuccess: (success) => {
        if (success) {
          queryClient.invalidateQueries({ queryKey: ['auth'] });
        }
      },
      onError: (error) => {
        console.error('Login failed:', error);
      },
    }
  );

  // Logout mutation using the auth service
  const logoutMutation = useApiMutation<void, void>(
    async () => {
      await authService.logout();
    },
    {
      onSuccess: () => {
        queryClient.clear(); // Clear all cached data
      },
      onError: (error) => {
        console.error('Logout failed:', error);
        // Clear cache anyway
        queryClient.clear();
      },
    }
  );

  // Check authentication status
  const checkAuthStatus = async () => {
    try {
      const isAuth = await authService.isAuthenticated();
      if (!isAuth) {
        queryClient.clear();
      }
      return isAuth;
    } catch (error) {
      console.error('Auth status check failed:', error);
      return false;
    }
  };

  const isAuthenticated = !!user && !error;

  return {
    user: user as AuthUser | undefined,
    isAuthenticated,
    isLoading,
    error,
    login: loginMutation.mutate,
    logout: logoutMutation.mutate,
    isLoggingIn: loginMutation.isPending,
    isLoggingOut: logoutMutation.isPending,
    checkAuthStatus,
    refetch,
  };
}

