          <Text style={styles.subtitle}>{t('actions.scan.description')}</Text>
        </View>

        <View style={styles.options}>
          <Card style={styles.optionCard}>
            <View style={styles.cardContent}>
              <Text style={styles.optionIcon}>ðŸ“·</Text>
              <Text style={styles.optionTitle}>Take Photo</Text>
              <Text style={styles.optionDescription}>
                Use your camera to capture a document
              </Text>
              <Button
                variant="primary"
                size="lg"
                fullWidth
                onPress={handleTakePhoto}
                disabled={isLoading}
                style={styles.optionButton}
              >
                Open Camera
              </Button>
            </View>
          </Card>

          <Card style={styles.optionCard}>
            <View style={styles.cardContent}>
              <Text style={styles.optionIcon}>ðŸ–¼ï¸</Text>
              <Text style={styles.optionTitle}>Choose from Gallery</Text>
              <Text style={styles.optionDescription}>
                Select an existing photo from your gallery
              </Text>
              <Button
                variant="outline"
                size="lg"
                fullWidth
                onPress={handleChooseFromGallery}
                disabled={isLoading}
                style={styles.optionButton}
              >
                Browse Gallery
              </Button>
            </View>
          </Card>
        </View>

        <View style={styles.tips}>
          <Text style={styles.tipsTitle}>ðŸ“‹ Scanning Tips</Text>
          <Text style={styles.tip}>â€¢ Ensure good lighting</Text>
          <Text style={styles.tip}>â€¢ Keep the document flat</Text>
